import { data, questions } from './quiz';

export class Question extends React.Component {
    constructor(props) {
        super(props);
        let idx = 0;
    }


    render() {
        let q = JSON.parse(questions);
        var keys = Object.keys(q[0]);
        var rankey = keys[Math.floor(Math.random() * keys.length)]
        var question = q[0][rankey];


        return (
            <div>
                <h1>{question}</h1>
            </div>
        );
    }
    
}

