import React, { Component } from 'react';
// import './App.css';
class App extends Component {
  constructor(props) {
    super(props);
    this.state = { apiResponse: ""};
  }

  callAPI() {
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => this.setState({ apiResponse: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  data = `[{
    "SuddenlyISee": {
      "album": {
        "id": "2up3OPMp9Tb4dAKM2erWXQ",
        "images": [{
          "url": "https://upload.wikimedia.org/wikipedia/en/e/e1/Eyetothetelescope.jpg",
          "height": 300,
          "width": 300
        }],
        "name": "Eye to the Telescope",
        "release_date": "2004-12",
        "uri": "spotify:album:2up3OPMp9Tb4dAKM2erWXQ"
      },
      "artists": [{
        "genres": [
          "Rock"
        ],
        "id": "string",
        "name": "KT Tunstall",
        "type": "artist"
      }],
      "id": "string",
      "is_playable": true,
      "name": "Suddenly I See",
      "path": "C:/Users/18607/Github/spotify-music-quiz/Downloads/SuddenlyISee.mp3"
    },
    "AmericanBoy": {
      "album": {
        "id": "2up3OPMp9Tb4dAKM2erWXQ",
        "images": [{
          "url": "https://upload.wikimedia.org/wikipedia/en/e/e1/Eyetothetelescope.jpg",
          "height": 300,
          "width": 300
        }],
        "name": "Shine",
        "release_date": "2008-01",
        "uri": "spotify:album:2up3OPMp9Tb4dAKM2erWXQ"
      },
      "artists": [{
          "genres": [
            "R&B",
            "Soul"
          ],
          "id": "string",
          "name": "Estelle",
          "type": "artist"
        },
        {
          "genres": [
            "Hip-Hop"
          ],
          "id": "string",
          "name": "Kanye West",
          "type": "artist"
        }
      ],
      "id": "string",
      "is_playable": true,
      "name": "American Boy",
      "path": "C:/Users/18607/Github/spotify-music-quiz/Downloads/AmericanBoy.mp3"
    },
    "BeautifulGirls": {
      "album": {
        "id": "2up3OPMp9Tb4dAKM2erWXQ",
        "images": [{
          "url": "https://upload.wikimedia.org/wikipedia/en/e/e1/Eyetothetelescope.jpg",
          "height": 300,
          "width": 300
        }],
        "name": "Sean Kingston",
        "release_date": "2007-12",
        "uri": "spotify:album:2up3OPMp9Tb4dAKM2erWXQ"
      },
      "artists": [{
        "genres": [
          "R&B",
          "Soul"
        ],
        "id": "string",
        "name": "Sean Kingston",
        "type": "artist"
      }],
      "id": "string",
      "is_playable": true,
      "name": "Beautiful Girls",
      "path": "C:/Users/18607/Github/spotify-music-quiz/Downloads/BeautifulGirls.mp3"
    },
    "River": {
      "album": {
        "id": "2up3OPMp9Tb4dAKM2erWXQ",
        "images": [{
          "url": "https://upload.wikimedia.org/wikipedia/en/e/e1/Eyetothetelescope.jpg",
          "height": 300,
          "width": 300
        }],
        "name": "Blue",
        "release_date": "1971-06",
        "uri": "spotify:album:2up3OPMp9Tb4dAKM2erWXQ"
      },
      "artists": [{
        "genres": [
          "Folk"
        ],
        "id": "string",
        "name": "Joni Mitchell",
        "type": "artist"
      }],
      "id": "string",
      "is_playable": true,
      "name": "River",
      "path": "C:/Users/18607/Github/spotify-music-quiz/Downloads/River.mp3"
    }
  }]`;
  
  load() {
      var json = JSON.parse(data);
      return json;
  }
  
  getRandomSong(songs){
      // idx = Math.floor(Math.random() * Object.keys(songs[0]).length);
      var keys = Object.keys(songs[0]);
      var rankey = keys[Math.floor(Math.random() * keys.length)]
      var song = songs[0][rankey];
      return song;
  }
  
  generateQuestion(songs){
  
      var answer = getRandomSong(songs);
      var numSongs = 0;
      var songArr = [answer];
      while (numSongs < 3) {
          var song = getRandomSong(songs);
          if (!songArr.includes(song)) {
              songArr.push(song);
              numSongs++;
          }
      }
  
      console.log(`What year did "${answer.name}" come out?`);
      console.log(`A. ${songArr[0].album.release_date}`);
      console.log(`B. ${songArr[1].album.release_date}`);
      console.log(`C. ${songArr[2].album.release_date}`);
      console.log(`D. ${songArr[3].album.release_date}`);
  }
  

  render() {
    return (
      <div className='App'>
        <header className='App-header'>
          <h1>Quiz Time</h1>
        </header>
      </div>
    );
  }
}

export default App;
